package operators;

public class UnaryOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		//increment
		System.out.println(a);
		System.out.println(a++);
		System.out.println(a);
		
		//increment
		System.out.println(++a);
		
		//decrement
		System.out.println(a--);
		System.out.println(a);
		System.out.println(--a);
		System.out.println(a);
		System.out.println(--a);
		
		
		
	}

}
